mapper.insert:
insert into program_card (card_id, trainer_id, date, 
  duration, trainee_id, confirmed
  )
values (#{cardId,jdbcType=INTEGER}, #{trainerId,jdbcType=INTEGER}, #{date,jdbcType=DATE}, 
  #{duration,jdbcType=INTEGER}, #{traineeId,jdbcType=INTEGER}, #{confirmed,jdbcType=BIT}
  )

mapper.select:
select card_id, trainer_id, date, duration, trainee_id, confirmed
from program_card
where card_id = #{cardId,jdbcType=INTEGER}
